/** 
 * Models used in the badges service 
 */

/** 
 * A Model for the standard Badge.
 * This includes informations on the likes and on the awarding.
 */
export class Badge {
    /** @type {string} title - The title of the badge */
    public title: string;
    /** @type {string} icon - The icon url of the badge */
    public icon: string;
    /** @type {string} description - The description of the badge */
    public description: string;
    /** @type {string} id - The id of the badge */
    public id: string;
    /** @type {string} awardedId - The id of the correspondent awarded badge */
    public awardedId: string;
    /** @type {boolean} awardedStatus - The status of the badge, true if awarded */
    public awardedStatus: boolean;
    /** @type {string} awardedDate - The date of the awarding */
    public awardedDate: string;
    /** @type {boolean} awardedTap - True if the badge have the like */
    public awardedTap: boolean;
    /** @type {number} likeNumber - The number of likes on the badge */
    public likeNumber: number;

    constructor(
      title: string, 
      icon: string, 
      description: string, 
      id: string,
      awardedId: string, 
      awardedStatus: boolean, 
      awardedDate: string, 
      awardedTap: boolean, 
      likeNumber: number
    ) {
        this.title = title;
        this.icon = icon;
        this.description = description;
        this.id = id;
        this.awardedId = awardedId;
        this.awardedStatus = awardedStatus;
        this.awardedDate = awardedDate;
        this.awardedTap = awardedTap;
        this.likeNumber = likeNumber;
      }
  }

  /**
  * A Model for the Awarded Badge.
  * This includes information on the likes.
  */
  export class AwardedBadge {
    /** @type {string} awardedId - The id of the awarded badge */
    public awardedId: string;
    /** @type {string} badgeId - The id of the correspondent badge */
    public badgeId: string;
    /** @type {boolean} alreadyLiked - True if the badge have the like */
    public alreadyLiked: boolean;
    /** @type {number} likeNumber - The number of likes */
    public likeNumber: number;
    /** @type {string} awardedDate - The date of awarding */
    public awardedDate: string;

    constructor(
      awardedId: string,
      badgeId: string,
      alreadyLiked: boolean,
      likeNumber: number,
      awardedDate: string
    ) {
        this.awardedId = awardedId;
        this.badgeId = badgeId;
        this.alreadyLiked = alreadyLiked;
        this.likeNumber = likeNumber;
        this.awardedDate = awardedDate;
      }
  }

  /* A Model for the like's informations. */
  export class likeInfo {
    /** @type {boolean} alreadyLike - True if there is the like */
    public alreadyLike: boolean;
    /** @type {string} likeId - The id of the like */
    public likeId: string;

    constructor(
      alreadyLike: boolean,
      likeId: string
    ) {
      this.alreadyLike = alreadyLike;
      this.likeId = likeId;
    }
  }

import { NgModule } from '@angular/core';
import { Http, HttpModule } from '@angular/http';
import { BadgesPluginService } from './badges-plugin.service';

@NgModule({
  imports: [HttpModule],
  providers: [BadgesPluginService]
})
export class BadgesPluginModule {

}

import { Injectable } from '@angular/core';
import { clinical6 } from 'clinical6';
import { Deserializer } from 'jsonapi-serializer';
import { Badge, AwardedBadge, likeInfo} from './badge-models';

/**
 * A Service for retrieving the badges.
 * This service use a matrix 3*m for storing and displaying the badges that is the one used in our projects.
 */
@Injectable()
export class BadgesPluginService {

  /** @type {Array} badges - All the badges available on the platform. */
  badges: Array<any>;
  /** @type {Array} recentBadges - The recent badges. */
  recentBadges: Array<any>;
  /** @type {Array} awardedBadges - The awarded badges. */
  awardedBadges: Array<any>;
  /** @type {boolean} loaded - A boolean variable that can be used to know if the data has been loaded. */
  loaded: boolean;

  constructor() {
  }

  /**
   * Use the init() method for starting the badges service.
   * @param {!boolean} likes - True if you need 'Likes' informations on the badges too, otherwise false.
   * @param {!boolean} companion - True if you are a companion, otherwise false if study_patient.
   * @param {!string} companionProfileId - If companion you must enter your profile id for fetching your 'Likes'.
   */
  async init(likes?: boolean, companion?: boolean, companionProfileId?: string) {
      this.loaded = false;
      this.badges = [];
      this.recentBadges = [];
      this.awardedBadges = [];
      try {
          await this.fetchBadges(likes, companion, companionProfileId);
      } catch (error) {
          console.log('Error initializing the badges service', error);
      }
  }

  /** 
   * The private method for retrieving all the badges informations.
   * @param {!boolean} likes - True if you need 'Likes' informations on the badges too, otherwise false.
   * @param {!boolean} companion - True if you are a companion, otherwise false if study_patient.
   * @param {!string} companionProfileId - If companion you must enter your profile id for fetching your 'Likes'.
   */
  private async fetchBadges(likes, companion, companionProfileId) {
      this.badges = [];
      this.recentBadges = [];
      this.awardedBadges = [];
      let allBadgesData;
      let awardedBadgesData;
      try {
          let _allBadgesData = await clinical6.fetch(`/v3/badges`, 'GET'); // fetching all the badges
          let deserializer = new Deserializer({keyForAttribute: 'camelCase'});
          allBadgesData = await deserializer.deserialize(_allBadgesData);
          awardedBadgesData = await this.fetchAwardedBadges(); // fetching the awarded badges
      } catch (error) {
          console.log('Error fetching badges informations', error);
      }
      if (likes) {
        try {
          for (let aw_item of awardedBadgesData.data ) {
            await this.fetchLikes(aw_item, companion, companionProfileId);
          }
          console.log('fetchBadges, data', allBadgesData);
          this.fillBadges(allBadgesData, likes);

        } catch (error) {
          console.log('Error fetching the awarded badges', error);
        }
      }
      else {
          // building the awarded badges array without the like informations
          for (let aw_item of awardedBadgesData.data ) {
              let fullAwardedBadge = new AwardedBadge(aw_item.id, aw_item.relationships.badge.data.id, false, 0, aw_item.attributes.earned_on_date);
              this.awardedBadges.push(fullAwardedBadge);
            }
          this.fillBadges(allBadgesData, likes);
      }
  }

  async fetchAwardedBadges() {
    try {
      let awardedBadgesData = await clinical6.fetch(`/v3/mobile_users/${clinical6.user.id}/badges`); // fetching the awarded badges
      return awardedBadgesData;
    } catch (error) {
      console.log('Error fetching awarded badges', error);
    }
  }

  /**
   * @param aw_item - The awarded badge.
   * @param {!boolean} companion - True if you are a companion, otherwise false if study_patient.
   * @param {!string} companionProfileId - If companion you must enter your profile id for fetching your 'Likes'.
   */
  async fetchLikes(aw_item, companion, companionProfileId) {
    try {
      let checkLike = await clinical6.fetch(`/v3/mobile_users/${clinical6.user.id}/badges/${aw_item.id}/likes`, 'GET'); // fetching the likes
            let alreadyLiked = false; // default value to false
            let likeNumber = 0;
            for ( let ch_item of checkLike.data ) { // checking if the like exists
              if ( companion ) { // if companion i need to know if the like exists
                if ( ch_item.relationships.mobile_user.data.id === companionProfileId
                    && ch_item.relationships.likeable.data.id === aw_item.id ) {
                  alreadyLiked = true;
                  break;
              }
              else alreadyLiked = false;
              }
              else if (!companion) { // if patient i need to know the number of likes
                if ( ch_item.relationships.likeable.data.id === aw_item.id ) {
                  likeNumber++;
                }
              }
            } 
            // building the awarded badges array with the like informations
            let fullAwardedBadge = new AwardedBadge(aw_item.id, aw_item.relationships.badge.data.id, alreadyLiked, likeNumber, aw_item.attributes.earned_on_date);
            this.awardedBadges.push(fullAwardedBadge);

    } catch (error) {
      console.log('Error fetching likes', error);
    }
  }

  /**
   * @param allBadgesData - All the badges on the platform.
   * @param {!boolean} likes - True if you need 'Likes' informations on the badges too, otherwise false.
   */
  private fillBadges(allBadgesData, likes?) {
      for (let badge of allBadgesData) {
          this.fillArray(this.badges, badge, likes); // fill all the badges
      }
      for (let row of this.badges) {
          for (let fullBadge of row) {
              if (fullBadge.awardedDate) {
                  this.fillRecentArray(this.recentBadges, fullBadge); // fill only the recent badges
              }
          }
      }
      this.loaded = true;
  }

  /**
   * @param badgesArray - All the badges on the platform.
   * @param badge - A single badge.
   * @param {!boolean} likes - True if you need 'Likes' informations on the badges too, otherwise false.
   */
  private fillArray(badgesArray, badge, likes?) {
      if(likes) {
          if (!badgesArray[0]) {
              badgesArray.push([]);
            }
            let last = badgesArray.length - 1;
            if ( badgesArray[last].length !== 0 && badgesArray[last].length % 2 === 0 ) {
              badgesArray.push([]);
                }
              let awardedInfo = this.findAwardedInfo(badge.id, likes);
              let awardedStatus, awardedTap, awardedId, image_earned, awardedDate, likeNumber;
              if ( awardedInfo ) {
                awardedStatus = true;
                awardedTap = awardedInfo.tap;
                awardedId = awardedInfo.id;
                image_earned = badge.image.url;
                awardedDate = awardedInfo.date;
                likeNumber = awardedInfo.number;
              }
              else {
                awardedStatus = false;
                awardedTap = false;
                awardedId = null;
                image_earned = 'assets/images/badge_missing.png';
                awardedDate = null;
                likeNumber = 0;
              }
              let fullBadge = new Badge(badge.title, image_earned, badge.description, 
                                          badge.id, awardedId, awardedStatus, 
                                          awardedDate, awardedTap, likeNumber);
              badgesArray[last].push(fullBadge);
      }
      else {
          if (!badgesArray[0]) {
              badgesArray.push([]);
            }
            let last = badgesArray.length - 1;
            if ( badgesArray[last].length !== 0 && badgesArray[last].length % 2 === 0 ) {
              badgesArray.push([]);
                }
              let awardedInfo = this.findAwardedInfo(badge.id);
              let awardedStatus, awardedTap, awardedId, image_earned, awardedDate, likeNumber;
              if ( awardedInfo ) {
                awardedStatus = true;
                awardedId = awardedInfo.id;
                image_earned = badge.image.url;
                awardedDate = awardedInfo.date;
              }
              else {
                awardedStatus = false;
                awardedId = null;
                image_earned = 'assets/images/badge_missing.png';
                awardedDate = null;
              }
              let fullBadge = new Badge(badge.title, image_earned, badge.description, 
                                          badge.id, awardedId, awardedStatus, 
                                          awardedDate, awardedTap, likeNumber);
              badgesArray[last].push(fullBadge);
      }

    }

   /**
    * @param recentBadgesArray - The array of recent badges.
    * @param badge - A single badge.
    */
    private fillRecentArray(recentBadgesArray, badge) {
      if (!recentBadgesArray[0]) {
        recentBadgesArray.push([]);
      }
      let last = recentBadgesArray.length - 1;
      recentBadgesArray[last].push(badge);
    }

    /**
     *  For adding informations on the awarded badges items 
     * @param id - The id of the awarded badge.
     * @param {!boolean} likes - True if you need 'Likes' informations on the badges too, otherwise false.
     */
    private findAwardedInfo(id, likes?) {
        if (likes) {
          for (let item of this.awardedBadges ) {
              if ( item.badgeId === id.toString() ) {
                return {
                  id: item.awardedId,
                  tap: item.alreadyLiked,
                  date: item.awardedDate,
                  number: item.likeNumber
                };
              }
            }
        }
        else {
          for (let item of this.awardedBadges ) {
              if ( item.badgeId === id.toString() ) {
                return {
                  id: item.awardedId,
                  tap: null,
                  date: item.awardedDate,
                  number: null
                };
              }
            }
        }
    }

    get(type, i, j) {
      let badgesData = this.badges;
      if (type === 'recentBadges') badgesData = this.recentBadges;
      return badgesData[i][j];
    }

    checkDisabled(type, i, j, direction) {
      let badgesData = this.badges;
      if (type === 'recentBadges') badgesData = this.recentBadges;
      let disabled = false;
      if (direction === 'prev') {
        if ( i === 0 && j === 0 ) disabled = true;
      }
      else {
        if ( (i === badgesData.length - 1) && j === (i === badgesData[i].length - 1) )
          disabled = true;
        else if ( (i === badgesData.length - 1) &&  badgesData[i][j + 1] && badgesData[i][j + 1].title === null )
          disabled = true;
      }
      return disabled;
    }

    next(type, i, j) {
      let badgesData = this.badges;
      if (type === 'recentBadges') badgesData = this.recentBadges;
      let row = i;
      let col = j;
      if (col < badgesData[row].length - 1) {
        if ( badgesData[row][col + 1].title )
          col++;
      }
      else if ( row < badgesData.length - 1 ) {
        col = 0;
        row++;
      }
      return {
        item: badgesData[row][col],
        i: row,
        j: col
      };
    }

    prev(type, i, j) {
      let badgesData = this.badges;
      if (type === 'recentBadges') badgesData = this.recentBadges;
      let row = i;
      let col = j;
      if (col > 0) {
        col--;
      }
      else if ( row > 0 ) {
        row--;
        col = badgesData[row].length - 1;
      }
      return {
        item: badgesData[row][col],
        i: row,
        j: col
      };
    }

    updateLike(id) {
        for ( let item of this.badges ) {
          for ( let el of item ) {
            if ( item.awardedId && item.awardedId === id ) {
              item.awardedTap = true;
              item.awardedStatus = true;
            }
          }
        }
    }

    /**
     * Methods for like, unlike and checking the like 
     * @param {!Badge} currBadge - The selected badge.
     * @param {!boolean} companion - True if you are a Companion.
     * @param {!string} companionProfileId - If companion you must enter your profile id for fetching your 'Likes'.
     */
    async like(currBadge: Badge, companion?, companionProfileId?: string) {
      let check = await this.checkLike(currBadge, companion, companionProfileId);
      try {
        if (!check.alreadyLike) {
          let data = {
            data : {
              type: 'likes',
              relationships: {
                mobile_users: {
                    data: {
                        type: 'mobile_users',
                        id: companionProfileId
                  }
                },
                likeable: {
                    data: {
                        type: 'awarded_badges',
                        id: currBadge.awardedId // this should be the selected awarded badge
                  }
                }
              }
            }
          };
          let response = await clinical6.fetch(`/v3/mobile_users/${clinical6.user.id}/badges/${currBadge.awardedId}/likes`, 'POST', data);
        }
        else if (check.alreadyLike) {
          let response = await clinical6.fetch(`/v3/mobile_users/${clinical6.user.id}/badges/${currBadge.awardedId}/likes/${check.likeId.toString()}`, 'DELETE');
        }
        let _check = await this.checkLike(currBadge, companion, companionProfileId);
      } catch (error) {
        console.log('Error performing Like for badge', error);
      }
    }

    /**
     * Methods for like, unlike and checking the like 
     * @param {!Badge} currBadge - The selected badge.
     * @param {!boolean} companion - True if you are a Companion.
     * @param {!string} companionProfileId - If companion you must enter your profile id for fetching your 'Likes'.
     */
    async checkLike(currBadge: Badge, companion?: boolean, companionProfileId?: string) {
      if ( currBadge.awardedId ) {
        try {
          let response = await clinical6.fetch(`/v3/mobile_users/${clinical6.user.id}/badges/${currBadge.awardedId}/likes`, 'GET');
          if (response.data.length > 0) {
            let check = new likeInfo(false, '');
            for ( let item of response.data ) {
              if ( companion ) {
                if ( item.relationships.mobile_user.data.id === companionProfileId && item.relationships.likeable.data.id === currBadge.awardedId ) {
                  check.alreadyLike = true;
                  check.likeId = item.id;
                  return check;
                }
              }
            }
            return check;
          }
          else {
            let check = new likeInfo(false, '');
            return check;
          }
        }
        catch (error) {
          console.log('Error checking Like', error);         
        }
      }
    }
}

import { Injectable } from '@angular/core';
import { Events } from 'ionic-angular';
// import { PROFILE_DEMO } from './mocks/';
import {
  clinical6,
  contentService,
  flowService,
  appMenuService,
  userService,

  Notification,
  Flow,
  Profile,
  systemService,
  AppMenu,
  Client,
  Content
} from 'clinical6';
import { AppConfig } from './config/app.config';

declare var fabric;

@Injectable()
export class Clinical6Service {

  supportContactInfo: any = {
    title: '',
    image: '',
    image_backup: '',
    description: '',
    phone_number: '',
    email_support: '',
  };
  userID: string;
  siteID: string;
  profile: any;
  xAuth_token: string;
  // mobile_user: any;

  /**
   * TODO: once V3 is integrated this won't be needed anymore
   */
  useDynContentV2: boolean = false;

  mobileMenus: any;
  menuBadges: any = {Surveys: 0, Notifications: 0, ManageSiteUpdates: 0 };
  notifications: Array<any>;
  flows: { [key: string]: Flow } = { };

  constructor(
    public events: Events,
    public appConfig: AppConfig,
  ) {
  }

  /**
   * Get Notifications
   *
   * @return {Promise<Notification[]>}
   */

  async getNotifications(): Promise<Notification[]> {
    try {
      const notifications = await userService.getNotifications(undefined, undefined);
        this.notifications = notifications;
        this.menuBadges.Notifications = notifications.filter(item => (item.status !== 'read')).length;
        return this.notifications;
      } catch (e) {
        throw('Notifications failed');
      }
  }

  /**
   * Get Dynamic Content
   * @param {String} className Name of class to be queries
   * @param {Number} page Page number currently on (0 is all)
   * @param {Number} itemCount Total number of items on a page
   * @return {Promise} Promise for use with Angularjs
   */
  async getDynamicContent(className: string, page: number = 0, itemCount: number = 0) {
      /**
       * TODO: once V3 is integrated this won't be needed anymore
       */
      if (this.useDynContentV2) {
        try {
          var httpQuery = '?page=0' + page + '&per_page=' + itemCount;
          const response = await Client.instance.fetch('/api/dynamic/' + className + httpQuery);
          var result;
          result = response.content.map(function (obj) {
            return new Content(obj);
          });
          return result;
        } catch (err) {
          throw(err);
        }
      }
      else {
        try {
          const data = await contentService.getContent(className);
          return data;
        } catch(e) {
          this.onError(e);
          throw('Dynamic Content Failed');
        }
      }
  }


  /**
   * Get Flow
   * @param {String} id The flow for a certain item
   */
  async getFlow(id: string) {
    try {
      const flow = await flowService.getFlow(id);
      return flow;
    } catch(reason) {
      this.onError(reason);
      throw(reason);
    }
  }

  /**
   * Get Menus (Mobile Menus)
   * @return {Promise} Promise for use with Angularjs
   */
  async getMenus(useOldApi?): Promise<AppMenu[]> {
      if (this.mobileMenus && this.mobileMenus.length > 0) {
        return this.mobileMenus;
      } else {
        try {
          let menus;
          if (useOldApi) {
            let response = await clinical6.fetch(`/api/v2/mobile_menus?include_subcategories=true`);
            menus = response.json.mobile_menus;
          } else {
            menus = await appMenuService.get();
          }
          this.mobileMenus = Object.keys(menus).map(key => menus[key]);
          if (!this.menuBadges) {
            this.menuBadges = {};
          }
          return this.mobileMenus;
        } catch (e) {
          this.onError(e);
          throw('Mobile Menus failed');
        }
      }
  }

  /**
   * Get Profile
   * @return {Promise} Promise for use with Angularjs
   */
  async getProfile(refresh?: boolean) {
    // if (AppConfig.demoMode) {
    //   return new Promise ( resolve => PROFILE_DEMO );
    // }
    if (this.profile && !refresh) {
      return this.profile;
    } else {
        try {
          const profile = await userService.getProfile();
            this.profile = profile as Profile;
            // sessionStorage.setItem('profile', angular.toJson(profile));
            return this.profile;
          }
          catch(reason) {
            this.onError(reason);
            throw(reason);
          }
      }
  }

  /**
   * Get Site
   * @param {string} permanent_link - Permanent Link for site (given by user at login)
   */
  async _getSite(permanent_link: string) { // not used for the time being, see AppService
    try {
      const data = await clinical6.fetch('/api/v2/site_start/sites');
        const site = data.sites.filter(obj => (obj.permanent_link === permanent_link))[0];
        if (site) {
          return site;
        } else {
          throw({ message: 'Could not find the site from server.', error_details: 'No match with the given site id' });
        }
      }
      catch(reason) {
        this.onError(reason);
        throw(reason);
      }
  }

  /**
   * Logs in the user
   * @param {string} username The username required for login
   * @param {string} password the password required for login
   * @return {Promise} Promise for use with Angularjs
   */
  async login(username: string, password: string) {
    try {
        const data = await clinical6.signIn(username, password);
        localStorage.setItem('authToken', data.auth_token);
        try {
          const profile = await this.getProfile(true);
          if (fabric && fabric.Crashlytics) {
            fabric.Crashlytics.setUserIdentifier(username);
          }
            return profile;
          }
          catch(reason) {
              throw({ message: reason.friendly, error_details: reason.details });
          }
      }
      catch(reason) {
          throw(reason);
      }
  }

  /**
   * Logs out the user
   * @return {Promise} Promise for use with Angularjs
   */
  async logout() {
    try {
      await clinical6.signOut();
      localStorage.clear();
      delete this.profile;
      this.events.publish('user:logout');
      return 'Successfully Logged Out';
    }
    catch(e) {
      localStorage.clear();
      delete this.profile;
      this.events.publish('user:logout');
      throw('Logout Failed');
    }
  }

  /**
   * Set the pin for a confirmation pin token
   * @param {string} pin a user input to set a pin
   * @param {string} pin_confirmation a token provided by the xauth to setup a pin.
   */
  async setPin(pin: string, pin_confirmation: string) {
    try {
      const params = { 'pin': pin, 'pin_confirmation': pin_confirmation, 'invitation_token': this.xAuth_token };
      // clinical6.fetch('/api/mobile_users/set_pin ', 'PUT', params ).then( data => {
      // clinical6.fetch('/api/mobile_users/accept_invitation ', 'PUT', params ).then( data => {
      const data = await userService.acceptInvitation(this.xAuth_token, pin);
      console.log('setPin, result: ', data);
      try {
          // login
          const data = await this.login(this.userID, pin);
          return 'PIN Successfully Entered and successfully logged in';
      } catch(reason) {
              console.log('Pin successsfully entered by login failed ' + reason.message + ' ' + reason.error_details);
              throw(reason);
      }
    }
    catch(reason) {
          console.log('Set Pin failed ' + reason.message + ' ' + reason.error_details);
          throw(reason);
    }
  }

  /**
   * On Error (Event)
   * @param  {Error} e Error object returned from server
   * @return {null} returns nothing
   */
  onError(e: any) {
    const self = this;
    const code = {
      INVALID_AUTHTOKEN: 50361
    };

    if (fabric && fabric.Crashlytics) {
      fabric.Crashlytics.addLog('ERROR: ' + JSON.stringify(e));
      fabric.Crashlytics.sendNonFatalCrash('ERROR: ' + JSON.stringify(e));
    }


    if (e.internal_code) {
      switch (e.internal_code) {
        case code.INVALID_AUTHTOKEN:
          console.log('API error, case of invalid token, logging out.');
          self.logout();
          break;
        default:
          break;
      }
    }
  }

  /**
   * silentSignIn()
   * sets the Clinical6 auth-token from the local storage
   * @return {boolean} returns true if successfull, false otherwise
   */
  silentSignIn() {
    if (localStorage.getItem('authToken')) {
      clinical6.authToken = localStorage.getItem('authToken');
      return true;
    }
    else {
      console.log('silentSignIn failed, the authToken was not stored');
      return false;
    }
  }
}

