import { Component } from '@angular/core';

import { ElementRef } from '@angular/core';
import { NavController, AlertController, NavParams, Platform } from 'ionic-angular';
import { BasePage } from '../base/base-page';
import { TranslatorService } from '../../translator/translator.service';
import { mobileUserService, flowService, userService, clinical6, Client } from 'clinical6';
import { AppConfig } from '../../config';
import { AppLoginService } from '../../login.service';

@Component({
  selector: 'create-new-user',
  templateUrl: 'create-new-user.html'
})
export class CreateNewUserPage extends BasePage {

  email: string = '';
  pin: string = '';
  account_name: string = '';
  working: boolean = false;
  isNavigating: boolean = false;
  constructor(
          public loginSvc: AppLoginService,
          public nav: NavController,
          public alertCtrl: AlertController,
          public params: NavParams,
          public platform: Platform,
          public el: ElementRef,
          public navCtrl: NavController,
          private translator: TranslatorService,
          ) {
      super(platform, el, navCtrl);
  }


  /**
   * This is called when the user clicks on "LOGIN" or equivalent action
   */
  doneCallback() {

    if (!this.isNavigating) {
      this.isNavigating = true;

      this.loginSvc.clearAuthInfo();
      this.loginSvc.authenticateDevice()
      .then(() => {

        var attrs = {};
        if (this.email) attrs['email'] = this.email;
        if (this.pin) attrs['password'] = this.pin;
        if (this.account_name) attrs['account_name'] = this.account_name;

      const url = `/v3/mobile_users/registrations`;
      const data = {
          'type': 'mobile_users',
          'attributes': attrs,
          'relationships': {
            'devices': {
              'data': {
                'type': 'devices',
                'id': Client.instance.device.id
              }
            }
          }
        };

      mobileUserService.register(attrs, Client.instance.device)
        .then((result) => {
          this.isNavigating = false;

          var buttons = [{
                  text: this.translator.getInnerHTML('POPUP_OK') || 'OK',
                  handler: data => {

                    this.nav.pop();

                  }
              }
            ];
          if (!this.pin && this.email) {
            buttons.push({
                  text: 'Send Confirmation Email',
                  handler: data => {
                    mobileUserService.sendConfirmation(this.email).then(() => {
                        this.showAlert('Success', 'User Created and email sent');
                    }).catch ((result) => {
                        this.showAlert('Error', 'User Created but email not sent:' + result.message);
                    });
                  }
            });
          }

          let alert = this.alertCtrl.create ({
            title: 'Success',
            message: 'User Successfully created',
            cssClass: 'code-alert',
            buttons: buttons
          });
          alert.present();
        })
        .catch((result) => {
          this.isNavigating = false;
          this.showAlert('Error', 'Error received from platform' + result.message);
        });
      });
    }
  }
  showAlert (title: string, msg: string) {

    let alert = this.alertCtrl.create ({
      title: title,
      message: msg,
      cssClass: 'code-alert',
      buttons: [
        {
            text: this.translator.getInnerHTML('POPUP_OK') || 'OK',
            handler: data => {

              this.nav.pop();

            }
        }
      ]
    });
    alert.present();
  }

}

export class AlertsFactory {

  /** @param ActionsMap Data structure for storing an action and the related page.  */
  static ActionsMap: Array<{
    action: string,
    page: any
    }> = [];

  /** @param DashboardPageName Variable for storing the dashboard page name. */
  static DashboardPageName: string;

    /**
   * Should be used in the app.component file to associate an action with a specific page to open.
   * @param item Object containing an Action and a Page.
   */
  static setAction(item: {action: string, page: any}) {
    this.ActionsMap.push(item);
  }

  /**
   * Should be used in the app.component file to specify the name of the dashboard page.
   * @param dashboardPageName 
   */
  static setDashboardNameForRoute(dashboardPageName: string) {
    this.DashboardPageName = dashboardPageName;
  } 
}

import { Injectable } from '@angular/core';
import { Client, clinical6, Notification, mobileUserService } from 'clinical6';
import { NavController } from 'ionic-angular';
import { AppConfig } from '../../config';
import { PushService } from './push.service';
import { AlertsFactory } from './alert.factory';
import { NgZone } from '@angular/core';
import { shimHostAttribute } from '@angular/platform-browser/src/dom/dom_renderer';

@Injectable()
export class AlertsService {

  public alerts: Notification[];
  animationOpt: any;
  constructor(
    private ngZone: NgZone
  ) {
    this.animationOpt = AppConfig.animationOpt;
  }

  private userId() {
    return clinical6.user.id;
  }

  /**
   * Retrieve all the alerts.
   */
  async getAlerts(): Promise<Notification[]> {
    try {
      let response = await mobileUserService.getNotifications();
      this.alerts = response.filter(e => {
        return e.attributes.status !== 'completed';
      }).map(element => {
        console.log(element);
        return new Notification(element);
      });
      console.log(this.alerts);
      return this.alerts;
    } catch (error) {
      console.log('Error in getAlerts', error);
    }
  }

  /**
   * Returns cached alerts instead of performing a new request.
   */
  cachedAlerts(): Promise<any[]>  {
    if (this.alerts) {
      return Promise.resolve(this.alerts);
    }
    return this.getAlerts();
  }

  /**
   * Remove the selected alert.
   * @param {Notification} message The selected Notification
   */
  async removeAlert(message: Notification) {
    try {
      message.status = 'completed';
      await message.save();
        return this.alerts = this.alerts.filter(curr => {
          return (curr.id !== message['id']);
        });
    } catch (error) {
      console.log('Error in removeAlert', error);
    }
  }

  // cannot inject the nav controller into the service
  /**
   * Used for the alert routing. Opens the page associated to the action.
   * @param nav Navigation Controller.
   * @param data Callback data that comes from platform.
   */
  goTakeAction(nav: NavController, data) {
    this.ngZone.run(async () => {

      let _action = data.content ? data.content.action : data.action ?  data.action : null;
      let _content = data.content ? data.content.action_object : data.action_object ? data.action_object : null;
      var matchingItem = AlertsFactory.ActionsMap.find((item) => {
        return item.action === _action;
      });
      if (matchingItem) {
        let options = await this.callback();
        nav.push(matchingItem.page, options, this.animationOpt);
      }
      else {
        console.log(`could not handle alert with action '${_action}' and action_object '${_content}'`);
      }
  });
  }
  // override this to add several actions before redirecting the user to a new page
  async callback() {
    return {};
  }
}

import { Component } from '@angular/core';
import * as moment from 'moment';
import { NavController, ViewController, ModalController, NavParams, LoadingController } from 'ionic-angular';
