    moment = moment;
    demoMode = true;

    currentDate: string;
    todayDate: string;
    selectedDate;
    dateView: boolean = true;
    mode: string;
    monthAppointments = [];
    todayAppointments = [];
    todayAppointmentsDemo = [];
    monthAppointmentsDemo = [];
    records = [];
    isUpdating: boolean = false;
    tutorialData;
    tutorialVideoUrl = 'https://www.youtube.com/embed/OU-cIoqKw-0?rel=0&controls=1&showinfo=0&autoplay=1&start=2';
    items = [];
    cycles = [];
