import { Injectable } from '@angular/core';
import { App, LoadingController, Config } from 'ionic-angular';

@Injectable()
export class UtilsService {
    /**  @param {any} loader - pointer to the current instance of the LoadingController */
    loader: any;
    /**  @param {LoadingController} loadingController - Loading Controller instance */
    loadingController: LoadingController;

    constructor(loadingCtrl: LoadingController) {
        this.loadingController = loadingCtrl;
    }

    /**
     * @function presentLoading - this creates a new Loading Controller and shows a the loading indicator
     *
     * @param content                         - The html content for the loading indicator.
     * @param {boolean} dismissOnPageChange   - Whether to dismiss the indicator when navigating to a new page. Default true.
     * @param {boolean} enableBackdropDismiss - Whether the loading indicator should be dismissed by tapping the backdrop. Default true.
     * @param {String} spinner                - The name of the SVG spinner for the loading indicator. Optional
     **/
    presentLoading(content = 'Please wait...', dismissOnPageChange: boolean = true, enableBackdropDismiss: boolean = true, spinner?: string) {
        if (!this.loader) {
            this.loader = this.loadingController.create({
                content: content,
                dismissOnPageChange: dismissOnPageChange,
                enableBackdropDismiss: enableBackdropDismiss,
                spinner: spinner
            });
        }
        this.loader.present();
        this.loader.onDidDismiss(() => {
            this.loader = null;
            console.log('Dismissed loading');
        });
    }

    /**
     * @function presentLoading - Makes sure to destroy the existing Loading Controller
    **/
    dismissLoader() {
        if (this.loader) {
            this.loader.dismiss();
            this.loader = null;
        }
    }

}

import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { BLE } from '@ionic-native/ble';
import { BluetoothDevice } from './devices/bluetooth-device.model';
import { BluetoothFactory } from './bluetooth.factory';
import { bluetoothServices, bluetoothCharacteristics } from './bluetooth.utility';

@Injectable()
export class BLEService extends BLE {
  constructor(private ble: BLE) {
    super();
  }

  scan(services: string[], seconds: number): Observable<BluetoothDevice> {
    return Observable.create((observer) => {
      for (var i = 0; i < services.length; ++i ) {
        const service = services[i];
        if (bluetoothServices[service]) {
          services[i] = bluetoothServices[service].toString(16);
        }
      }
      this.ble.scan(services, seconds).subscribe(device => {
        observer.next(BluetoothFactory.create(device, this));
      });
    });
  }

  startScan(services: string[]): Observable<BluetoothDevice> {
    return Observable.create((observer) => {
      this.ble.startScan(services).subscribe(device => {
        observer.next(BluetoothFactory.create(device, this));
      });
    });
  }

  startScanWithOptions(services: string[], options: {
        reportDuplicates?: boolean;
    } | any): Observable<BluetoothDevice> {
    return Observable.create((observer) => {
      this.ble.startScanWithOptions(services, options).subscribe(device => {
        observer.next(BluetoothFactory.create(device, this));
      });
    });
  }

  connect(deviceId: string): Observable<BluetoothDevice> {
    return Observable.create((observer) => {
      this.ble.connect(deviceId).subscribe(device => {
        observer.next(BluetoothFactory.create(device, this));
      });
    });
  }

  read(deviceId: string, service: string, characteristic: string): Promise<any> {
    let _service: string = service;
    let _characteristic: string = characteristic;

    if (bluetoothServices[service]) {
      _service = bluetoothServices[service].toString(16);
    }
    if (bluetoothCharacteristics[characteristic]) {
      _characteristic = bluetoothCharacteristics[characteristic].toString(16);
    }
    return this.ble.read( deviceId, _service, _characteristic);
  }

  write(deviceId: string, service: string, characteristic: string, value: ArrayBuffer): Promise<any> {
    let _service: string = service;
    let _characteristic: string = characteristic;

    if (bluetoothServices[service]) {
      _service = bluetoothServices[service].toString(16);
    }
    if (bluetoothCharacteristics[characteristic]) {
      _characteristic = bluetoothCharacteristics[characteristic].toString(16);
    }
    return this.ble.write( deviceId, _service, _characteristic, value);
  }

  writeWithoutResponse(deviceId: string, service: string, characteristic: string, value: ArrayBuffer): Promise<any> {
    let _service: string = service;
    let _characteristic: string = characteristic;

    if (bluetoothServices[service]) {
      _service = bluetoothServices[service].toString(16);
    }
    if (bluetoothCharacteristics[characteristic]) {
      _characteristic = bluetoothCharacteristics[characteristic].toString(16);
    }
    return this.ble.writeWithoutResponse( deviceId, _service, _characteristic, value);
  }

  startNotification(deviceId: string, service: string, characteristic: string): Observable<any> {
    let _service: string = service;
    let _characteristic: string = characteristic;

    if (bluetoothServices[service]) {
      _service = bluetoothServices[service].toString(16);
    }
    if (bluetoothCharacteristics[characteristic]) {
      _characteristic = bluetoothCharacteristics[characteristic].toString(16);
    }
    return this.ble.startNotification( deviceId, _service, _characteristic);
  }

  stopNotification(deviceId: string, service: string, characteristic: string): Promise<any> {
    let _service: string = service;
    let _characteristic: string = characteristic;

    if (bluetoothServices[service]) {
      _service = bluetoothServices[service].toString(16);
    }
    if (bluetoothCharacteristics[characteristic]) {
      _characteristic = bluetoothCharacteristics[characteristic].toString(16);
    }
    return this.ble.stopNotification( deviceId, _service, _characteristic);
  }
}

import { bluetoothCharacteristics, bluetoothServices, getKeyFromValue } from './bluetooth.utility';

export class BluetoothCharacteristic {
  service: string;
  characteristic: string;
  properties: Array<string>;
  descriptors?: Array<any>;

  get characteristicName() {
    return getKeyFromValue(parseInt(this.characteristic, 16), bluetoothCharacteristics);
  }

  get serviceName() {
    return getKeyFromValue(parseInt(this.service, 16), bluetoothServices);
  }

  constructor(response?: any) {
    if (response) {
      Object.assign(this, response);
    }
  }
}

import { ForaGlucometer } from './devices/fora-glucometer.model';
import { BluetoothDevice } from './devices/bluetooth-device.model';
import { BLEService } from './ble.service';

export class BluetoothFactory {
  static getClass(name) {
    let device;
    switch (name) {
      case 'FORA PREMIUM V10':
        device = ForaGlucometer;
        break;
      default:
        device = BluetoothDevice;
        break;
    }
    return device;
}
  static create(response: any, ble: BLEService) {
    let device;
    if (response) {
      device = new (BluetoothFactory.getClass(response.name))(response, ble);
    }
    return device;
  }
}

